contracts/AdminManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @title AdminManager
 * @notice Reusable contract for managing administrative functions
 * @dev Can be inherited by any contract that needs admin functionality
 */
abstract contract AdminManager is OwnableUpgradeable {

    address public oracul;
    bool public paused;
    bool public withdrawalsEnabled;
    uint256 public withdrawalDelay;

    // Events
    event OracleUpdated(address indexed oldOracle, address indexed newOracle);
    event WithdrawalDelayUpdated(uint256 oldDelay, uint256 newDelay);
    event WithdrawalsEnabled(); // Can only be emitted once
    event Paused();
    event Unpaused();

    // Modifiers
    modifier onlyOracul() {
        require(msg.sender == oracul, "not an oracul");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "Contract is paused");
        _;
    }

    modifier whenWithdrawalsEnabled() {
        require(withdrawalsEnabled, "Withdrawals not enabled yet");
        _;
    }

    /**
     * @notice Initialize admin manager (call from inheriting contract's initialize)
     * @param _oracul Initial oracle address
     */
    function __AdminManager_init(address _oracul) internal onlyInitializing {
        require(_oracul != address(0), "Invalid oracle address");
        oracul = _oracul;
        paused = false;
        withdrawalsEnabled = false; // Start with withdrawals disabled
        withdrawalDelay = 7 days;
    }

    // ═══════════════════════════════════════════════════════════════════
    // ORACLE MANAGEMENT
    // ═══════════════════════════════════════════════════════════════════

    /**
     * @notice Set oracle address
     * @param _oracul New oracle address
     */
    function setOracul(address _oracul) external onlyOwner {
        require(_oracul != address(0), "Invalid oracle address");
        address oldOracle = oracul;
        oracul = _oracul;
        emit OracleUpdated(oldOracle, _oracul);
    }

    // ═══════════════════════════════════════════════════════════════════
    // WITHDRAWAL MANAGEMENT
    // ═══════════════════════════════════════════════════════════════════

    /**
     * @notice Enable withdrawals forever (can only be called once)
     * @dev Once enabled, withdrawals cannot be disabled again
     */
    function enableWithdrawals() external onlyOwner {
        require(!withdrawalsEnabled, "Withdrawals already enabled");
        withdrawalsEnabled = true;
        emit WithdrawalsEnabled();
    }


    // PAUSE MECHANISM
    // ═══════════════════════════════════════════════════════════════════

    /**
     * @notice Pause contract operations
     */
    function pause() external onlyOwner {
        paused = true;
        emit Paused();
    }

    /**
     * @notice Unpause contract operations
     */
    function unpause() external onlyOwner {
        paused = false;  
        emit Unpaused();
    }

    // ═══════════════════════════════════════════════════════════════════
    // WITHDRAWAL DELAY MANAGEMENT
    // ═══════════════════════════════════════════════════════════════════

    /**
     * @notice Set withdrawal delay period
     * @param _delay New delay in seconds (max 30 days)
     */
    function setWithdrawalDelay(uint256 _delay) external onlyOwner {
        require(_delay <= 30 days, "Delay too long");
        uint256 oldDelay = withdrawalDelay;
        withdrawalDelay = _delay;
        emit WithdrawalDelayUpdated(oldDelay, _delay);
    }

    // ═══════════════════════════════════════════════════════════════════
    // VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════

    /**
     * @notice Get admin configuration
     * @return oracul_ Oracle address
     * @return paused_ Whether contract is paused
     * @return withdrawalDelay_ Current withdrawal delay
     */
    function getAdminConfig() external view returns (address oracul_, bool paused_, uint256 withdrawalDelay_) {
        return (oracul, paused, withdrawalDelay);
    }

    /**
     * @notice Check if contract is operational (not paused)
     * @return operational True if contract is not paused
     */
    function isOperational() external view returns (bool operational) {
        return !paused;
    }
}

contracts/BridgeManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

// Interface for bridge operations
interface IBridge {
    function bridgeToken(address token_address, uint256 value, bytes3 destination, bytes calldata destination_address) external;
    function unlockBridgedToken(bytes32 txHash, bytes3 source, address token, address to, uint256 value) external;
    function unlocked(address sender, address token, uint value) external;
    function isUnlocked(bytes32 hash) external view returns(bool);
}

/**
 * @title BridgeManager
 * @notice Reusable contract for managing bridge operations
 * @dev Can be inherited by any contract that needs bridge functionality
 */
abstract contract BridgeManager is OwnableUpgradeable {

    IBridge public bridge;

    // Bridge settings for native chain
    bytes3 public nativeChainId; // Destination chain ID for CELL tokens
    bytes public validatorAddress; // Validator address in native chain where CELL tokens go

    // Events
    event BridgeUpdated(address indexed oldBridge, address indexed newBridge);
    event ValidatorAddressUpdated(bytes3 chainId, bytes validatorAddress);
    event TokensBridgedToValidator(address indexed user, uint256 amount, bytes3 destination, bytes validatorAddress);

    /**
     * @notice Initialize bridge manager (call from inheriting contract's initialize)
     * @param _bridge Bridge contract address
     * @param _nativeChainId Native chain ID
     * @param _validatorAddress Validator address
     */
    function __BridgeManager_init(
        address _bridge,
        bytes3 _nativeChainId,
        bytes calldata _validatorAddress
    ) internal onlyInitializing {
        require(_bridge != address(0), "Invalid bridge address");
        require(_nativeChainId != bytes3(0), "Invalid chain ID");
        require(_validatorAddress.length > 0, "Invalid validator address");

        bridge = IBridge(_bridge);
        nativeChainId = _nativeChainId;
        validatorAddress = _validatorAddress;
    }

    // ═══════════════════════════════════════════════════════════════════
    // BRIDGE MANAGEMENT
    // ═══════════════════════════════════════════════════════════════════

    /**
     * @notice Set bridge contract address
     * @param _bridge New bridge address
     */
    function setBridge(address _bridge) external onlyOwner {
        require(_bridge != address(0), "Invalid bridge address");
        address oldBridge = address(bridge);
        bridge = IBridge(_bridge);
        emit BridgeUpdated(oldBridge, _bridge);
    }

    /**
     * @notice Set validator address and chain ID
     * @param _chainId Target chain ID
     * @param _validatorAddress Validator address in target chain
     */
    function setValidatorAddress(bytes3 _chainId, bytes calldata _validatorAddress) external onlyOwner {
        require(_chainId != bytes3(0), "Invalid chain ID");
        require(_validatorAddress.length > 0, "Invalid validator address");
        nativeChainId = _chainId;
        validatorAddress = _validatorAddress;
        emit ValidatorAddressUpdated(_chainId, _validatorAddress);
    }

    // ═══════════════════════════════════════════════════════════════════
    // BRIDGE OPERATIONS
    // ═══════════════════════════════════════════════════════════════════

    /**
     * @notice Internal function to bridge tokens to validator
     * @param token Token address to bridge
     * @param amount Amount to bridge
     * @param user User address (for events)
     */
    function _bridgeToValidator(address token, uint256 amount, address user) internal {
        require(address(bridge) != address(0), "Bridge not configured");
        require(nativeChainId != bytes3(0), "Native chain not configured");
        require(validatorAddress.length > 0, "Validator address not configured");

        // Bridge tokens to native chain validator address
        bridge.bridgeToken(token, amount, nativeChainId, validatorAddress);

        emit TokensBridgedToValidator(user, amount, nativeChainId, validatorAddress);
    }


    // ═══════════════════════════════════════════════════════════════════
    // VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════

    /**
     * @notice Get bridge configuration
     * @return bridge_ Bridge contract address
     * @return nativeChainId_ Native chain ID
     * @return validatorAddress_ Validator address
     */
    function getBridgeConfig() external view returns (address bridge_, bytes3 nativeChainId_, bytes memory validatorAddress_) {
        return (address(bridge), nativeChainId, validatorAddress);
    }

    /**
     * @notice Check if bridge is properly configured
     * @return configured True if all bridge settings are set
     */
    function isBridgeConfigured() external view returns (bool configured) {
        return address(bridge) != address(0) && 
               nativeChainId != bytes3(0) && 
               validatorAddress.length > 0;
    }
}

contracts/ValoraCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./ValoraStakedCell.sol";
import "./AdminManager.sol";
import "./BridgeManager.sol";


contract ValoraCore is ReentrancyGuardUpgradeable, UUPSUpgradeable, AdminManager, BridgeManager {
    IERC20 public cellToken;
    ValoraStakedCell public sCellToken;

    uint256 private totalAssets;
    uint256 public constant MIN_DEPOSIT = 1e12; // Minimum deposit 0.000001 tokens (assuming 18 decimals)

    mapping(address => WithdrawalRequest) public withdrawalRequests;

    struct WithdrawalRequest {
        uint256 shares;
        uint256 unlockTime;
    }

    // Events
    event Staked(address indexed user, uint256 amount);
    event WithdrawRequested(address indexed user, uint256 shares, uint256 unlockTime);
    event WithdrawCompleted(address indexed user, uint256 amount);
    event Rebased(uint256 totalShares, uint256 totalAssets);

    function initialize(
        address _cellToken, 
        address _sCellToken, 
        address _oracul,
        address _bridge,
        bytes3 _nativeChainId,
        bytes calldata _validatorAddress
    ) external initializer {
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();
        __Ownable_init(msg.sender);
        __AdminManager_init(_oracul);
        __BridgeManager_init(_bridge, _nativeChainId, _validatorAddress);

        cellToken = IERC20(_cellToken);
        sCellToken = ValoraStakedCell(_sCellToken);
    }

    // ═══════════════════════════════════════════════════════════════════
    // CORE STAKING FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════

    function deposit(uint256 amount) external whenNotPaused nonReentrant {
        require(amount >= MIN_DEPOSIT, "Amount too small");

        // Transfer CELL tokens from user to this contract
        require(cellToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        // Approve bridge to spend CELL tokens
        require(cellToken.approve(address(bridge), amount), "Bridge approval failed");

        // Bridge CELL tokens to native chain validator address (inherited from BridgeManager)
        _bridgeToValidator(address(cellToken), amount, msg.sender);

        // Mint sCELL tokens to user
        _deposit(amount);

    }

    function _deposit(uint256 amount) private {
        uint256 supply = sCellToken.totalSupply();
        uint256 assets = totalAssets;

        uint256 shares;

        if (supply == 0 || assets == 0) {
            // Первый депозит: 1 share = 1 token
            shares = amount;
        } else {
            // Use precise calculation with rounding check
            shares = (amount * supply) / assets;
            require(shares > 0, "Deposit too small, would result in 0 shares");
        }

        sCellToken.mint(msg.sender, shares);

        totalAssets += amount;

        emit Staked(msg.sender, amount);
    }

    // --- Request Withdrawal ---
    function requestWithdrawal(uint256 shares) external whenNotPaused whenWithdrawalsEnabled nonReentrant {
        require(shares > 0, "Shares must be greater than 0");
        require(sCellToken.balanceOf(msg.sender) >= shares, "Insufficient shares");

        // User burns their own shares - no need for burnFrom
        require(sCellToken.transferFrom(msg.sender, address(this), shares), "Transfer failed");
        sCellToken.burn(shares);

        uint256 unlockTime = block.timestamp + withdrawalDelay;
        withdrawalRequests[msg.sender] = WithdrawalRequest({
            shares: shares,
            unlockTime: unlockTime
        });

        emit WithdrawRequested(msg.sender, shares, unlockTime);
    }

    // --- Complete Withdrawal ---
    function completeWithdrawal() external whenNotPaused whenWithdrawalsEnabled nonReentrant {
        WithdrawalRequest memory req = withdrawalRequests[msg.sender];
        require(req.shares > 0, "No pending withdrawal");
        require(block.timestamp >= req.unlockTime, "Still locked");

        uint256 amount = (req.shares * exchangeRate()) / 1e18;
        require(amount > 0, "Withdrawal amount too small");

        delete withdrawalRequests[msg.sender];

        _delAssets(amount);

        require(cellToken.transfer(msg.sender, amount), "Transfer failed");


        emit WithdrawCompleted(msg.sender, amount);
    }

    function _delAssets(uint256 amount) private {
        require(totalAssets >= amount, "Insufficient assets");
        totalAssets -= amount;
    }

    // --- Rebase ---
    function rebase(uint256 amount) onlyOracul external {
        require(amount > 0, "Amount must be greater than 0");
        uint256 oldAssets = totalAssets;

        // Prevent dramatic changes (>50% up or down) as protection against oracle manipulation
        if (oldAssets > 0) {
            require(amount >= oldAssets / 2, "Rebase decrease too large");
            require(amount <= oldAssets * 3 / 2, "Rebase increase too large");
        }

        totalAssets = amount;
        emit Rebased(sCellToken.totalSupply(), totalAssets);
    }

    // ═══════════════════════════════════════════════════════════════════
    // VIEW FUNCTIONS
    // ═══════════════════════════════════════════════════════════════════

    function exchangeRate() public view returns (uint256) {
        uint256 supply = sCellToken.totalSupply();
        if (supply == 0) return 1e18;
        return (totalAssets * 1e18) / supply;
    }

    function getTotalAssets() external view returns (uint256) {
        return totalAssets;
    }

    function getWithdrawalRequest(address user) external view returns (WithdrawalRequest memory) {
        return withdrawalRequests[user];
    }

    // ═══════════════════════════════════════════════════════════════════
    // UUPS UPGRADE AUTHORIZATION
    // ═══════════════════════════════════════════════════════════════════

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

contracts/ValoraStakedCell.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract ValoraStakedCell is ERC20, Ownable {
    address public coreContract;

    // Events
    event CoreContractUpdated(address indexed oldCore, address indexed newCore);

    constructor() ERC20("Valora Staked Cell", "sCELL") Ownable(msg.sender) {}

    modifier onlyCore() {
        require(msg.sender == coreContract, "Only core contract allowed");
        _;
    }

    function setCoreContract(address _core) external onlyOwner {
        require(_core != address(0), "Invalid core contract address");
        address oldCore = coreContract;
        coreContract = _core;
        emit CoreContractUpdated(oldCore, _core);
    }

    function mint(address to, uint256 amount) external onlyCore {
        require(to != address(0), "Cannot mint to zero address");
        require(amount > 0, "Amount must be greater than 0");
        _mint(to, amount);
    }

    function burn(uint256 amount) external onlyCore {
        require(amount > 0, "Amount must be greater than 0");
        _burn(address(this), amount);
    }
}

